import React, { useState } from 'react';
import axios from 'axios';

// Define a function to retrieve the CSRF token from cookies
const getCookie = (name) => {
    const cookieValue = document.cookie.match('(^|;)\\s*' + name + '\\s*=\\s*([^;]+)');
    return cookieValue ? cookieValue.pop() : null;
};

const Register = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const csrftoken = getCookie('csrftoken');
            // Send user registration data to backend API
            const response = await axios.post('http://localhost:8000/admin/', {
                firstName,
                lastName,
                username,
                email,
                password,
                verified: false // Mark the user as not verified initially
                
            });

            // Check if response contains data property
            if (response && response.data) {
                // Handle successful registration
                console.log('Registration successful:', response.data);
                
                // Reset form fields and error state
                setFirstName('');
                setLastName('');
                setUsername('');
                setEmail('');
                setPassword('');
                setError('');
            } else {
                throw new Error('Unexpected response format');
            }
        } catch (error) {
            // Handle registration error
            console.error('Registration error:', error.response?.data?.message || error.message);
            setError(error.response?.data?.message || error.message);
        }
    };

    return (
        <div>
            <h2>Register to BOOKSHELF BUDDY</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="firstName">First Name:</label>
                    <input type="text" id="firstName" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="lastName">Last Name:</label>
                    <input type="text" id="lastName" value={lastName} onChange={(e) => setLastName(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="username">Username:</label>
                    <input type="text" id="username" value={username} onChange={(e) => setUsername(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="email">Email:</label>
                    <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="password">Password:</label>
                    <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                </div>

                {/* Display error message if registration fails */}
                {error && <p className="error-message">{error}</p>}

                <button type="submit" className="btn btn-primary">Register</button>
            </form>
        </div>
    );
};

export default Register;
